import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { GenreModel } from 'src/app/models/genre-model';
import { DataService } from 'src/app/services/data.service';
import { NotifyService } from 'src/app/services/notify.service';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent implements OnInit {

    public genres: GenreModel[];
    public constructor(private dataService: DataService, private notifyService: NotifyService) { }

    @ViewChild("genreId")
    public genreIdRef: ElementRef<HTMLSelectElement>;

    public async ngOnInit() {
        try {
            this.genres = await this.dataService.getAllGenres();
        }
        catch(err: any) {
            this.notifyService.error(err);
        }
    }

    public handleChange(){
        const genreId = this.genreIdRef.nativeElement;
        console.log(genreId);
    }
}
