import { Component } from '@angular/core';
import { GameModel } from 'src/app/models/game-model';
import { DataService } from 'src/app/services/data.service';
import { NotifyService } from 'src/app/services/notify.service';

@Component({
    selector: 'app-insert',
    templateUrl: './insert.component.html',
    styleUrls: ['./insert.component.css']
})
export class InsertComponent {

    public game = new GameModel();
    public constructor(private dataService: DataService, private notifyService: NotifyService) { }


    public async handleSubmit(){
        console.log(this.game);
    }


}
<h3>Add new game:</h3>
  <form action="" (submit)="handleSubmit()">
  <input type="text" placeholder="name" [(ngModel)]="game.name">
  <input type="text" placeholder="description" [(ngModel)]="game.description">
  <input type="date" placeholder="release date" [(ngModel)]="game.releaseDate">
  <input type="number" placeholder="price" [(ngModel)]="game.price">
  <button>Submit</button>
</form>
