import { Component, OnInit, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { GameModel } from 'src/app/models/game-model';
import { GenreModel } from 'src/app/models/genre-model';
import { DataService } from 'src/app/services/data.service';
import { NotifyService } from 'src/app/services/notify.service';

@Component({
    selector: 'app-insert',
    templateUrl: './insert.component.html',
    styleUrls: ['./insert.component.css']
})
export class InsertComponent implements OnInit {

    public game = new GameModel();
    public genres: GenreModel[] = [];

    public constructor(
        private dataService: DataService, 
        private notifyService: NotifyService,
        private router: Router
        ) { }

    public async ngOnInit() {
        this.genres = await this.dataService.getAllGenres();
    }

    public async handleSubmit(){
       try {
        console.log(this.game);

        await this.dataService.addGame(this.game);
        this.notifyService.success("A new game has been added.")
        this.router.navigateByUrl('/list')
       } catch (err:any){
        this.notifyService.error(err);
        console.log(err);
       }
    }


}
<h3>Add new game:</h3>

  <form (ngSubmit)="handleSubmit()">

  <select name="genreId" [(ngModel)]="game.genreId">
    <option selected disabled>Select a genre...</option>
    <option *ngFor="let g of genres" [value]="g.genreId">{{g.genreName}}</option>
  </select>

  <input type="text" name="description" placeholder="description" [(ngModel)]="game.description">
  <input type="date" name="releaseDate" placeholder="release date" [(ngModel)]="game.releaseDate">
  <input type="number" name="price" placeholder="price" [(ngModel)]="game.price">
  <input type="text" name="name" placeholder="name" [(ngModel)]="game.name">

  <button>Submit</button>

</form>
