import { Component, Input, EventEmitter, Output } from '@angular/core';
import GameModel from 'src/app/models/game-model';

@Component({
  selector: 'app-game-card',
  templateUrl: './game-card.component.html',
  styleUrls: ['./game-card.component.css']
})
export class GameCardComponent {
  public editMode: boolean = false;
  public updatedGame = new GameModel();

  @Input()
    public game: GameModel;

  @Output()
  public deleteMe = new EventEmitter<string>();

  public deleteGame(){
    this.deleteMe.emit(this.game._id);
  }

  public editGame(){
    if( this.editMode === false ){
      this.editMode = true;
    } else {
      this.updatedGame = { ...this.game };
      console.log(this.updatedGame);

      this.editMode = false;
    }      
  }
}
<div class="card">
  <p>
    Game Title:
    <ng-container *ngIf="editMode else editName">
      <input type="text" [(ngModel)]="game.name" name="name">
    </ng-container>
    <ng-template #editName>{{ game.name }}</ng-template>
  </p>

  <p>
    Description: 
    <ng-container *ngIf="editMode else editDescription">
      <input type="text" [(ngModel)]="game.description" name="description">
    </ng-container>
    <ng-template #editDescription>{{ game.description }}</ng-template>
  </p>

  <p>
    Price:
    <ng-container *ngIf="editMode else editPrice">
      <input type="number" [(ngModel)]="game.price" name="price">
    </ng-container>
    <ng-template #editPrice>{{ game.price | currency }}</ng-template>
  </p>

  <button (click)="deleteGame()">Delete</button>

  <button (click)="editGame()">{{ editMode ? "Save" : "Edit" }}</button>
</div>
